// src/components/AccountDashboard.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const AccountDashboard = () => {
  const [account, setAccount] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [amount, setAmount] = useState("");
  const [targetAccountNumber, setTargetAccountNumber] = useState("");
  const [sortOrder, setSortOrder] = useState("desc");
  const [filterType, setFilterType] = useState("all");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    // Fetch account details
    axios
      .get("/api/accounts/1/")
      .then((response) => setAccount(response.data))
      .catch((error) => console.error("Error fetching account:", error));

    // Fetch account statement
    fetchTransactions();
  }, [sortOrder, filterType, startDate, endDate, page]);

  const fetchTransactions = () => {
    const params = {
      sort: sortOrder,
      transaction_type: filterType,
      start_date: startDate,
      end_date: endDate,
      page: page,
      items_per_page: itemsPerPage,
    };

    axios
      .get("/api/accounts/1/statement/", { params })
      .then((response) => {
        setTransactions(response.data.results);
        setTotalPages(response.data.total_pages);
      })
      .catch((error) => console.error("Error fetching statement:", error));
  };

  const handleDeposit = () => {
    axios
      .post("/api/accounts/1/deposit/", { amount: parseFloat(amount) })
      .then((response) => {
        alert(response.data.status);
        setAmount("");
        fetchTransactions();
      })
      .catch((error) => console.error("Error depositing money:", error));
  };

  const handleWithdraw = () => {
    axios
      .post("/api/accounts/1/withdraw/", { amount: parseFloat(amount) })
      .then((response) => {
        alert(response.data.status);
        setAmount("");
        fetchTransactions();
      })
      .catch((error) => console.error("Error withdrawing money:", error));
  };

  const handleTransfer = () => {
    axios
      .post("/api/accounts/1/transfer/", {
        amount: parseFloat(amount),
        target_account_number: targetAccountNumber,
      })
      .then((response) => {
        alert(response.data.status);
        setAmount("");
        setTargetAccountNumber("");
        fetchTransactions();
      })
      .catch((error) => console.error("Error transferring money:", error));
  };

  const handleSortOrderChange = (e) => {
    setSortOrder(e.target.value);
    setPage(1); // Reset to first page
  };

  const handleFilterChange = (e) => {
    setFilterType(e.target.value);
    setPage(1); // Reset to first page
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setPage(newPage);
    }
  };

  return (
    <div>
      {account && (
        <div>
          <h2>Account: {account.account_number}</h2>
          <h3>Balance: {account.balance}</h3>
        </div>
      )}
      <div>
        <h3>Actions</h3>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button onClick={handleDeposit}>Deposit</button>
        <button onClick={handleWithdraw}>Withdraw</button>
        <input
          type="text"
          value={targetAccountNumber}
          onChange={(e) => setTargetAccountNumber(e.target.value)}
          placeholder="Target Account Number"
        />
        <button onClick={handleTransfer}>Transfer</button>
      </div>
      <div>
        <h3>Account Statement</h3>
        <div>
          <label>Sort by Date:</label>
          <select value={sortOrder} onChange={handleSortOrderChange}>
            <option value="desc">Descending</option>
            <option value="asc">Ascending</option>
          </select>
        </div>
        <div>
          <label>Filter by Type:</label>
          <select value={filterType} onChange={handleFilterChange}>
            <option value="all">All</option>
            <option value="deposit">Deposits</option>
            <option value="withdrawal">Withdrawals</option>
            <option value="transfer">Transfers</option>
          </select>
        </div>
        <div>
          <label>Filter by Date Range:</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </div>
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Amount</th>
              <th>Type</th>
              <th>Related Account</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((transaction) => (
              <tr key={transaction.id}>
                <td>{new Date(transaction.date).toLocaleDateString()}</td>
                <td>{transaction.amount}</td>
                <td>{transaction.transaction_type}</td>
                <td>
                  {transaction.related_account
                    ? transaction.related_account.account_number
                    : "-"}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <button onClick={() => handlePageChange(1)}>First</button>
          <button onClick={() => handlePageChange(page - 1)}>Previous</button>
          <span>
            Page {page} of {totalPages}
          </span>
          <button onClick={() => handlePageChange(page + 1)}>Next</button>
          <button onClick={() => handlePageChange(totalPages)}>Last</button>
        </div>
      </div>
    </div>
  );
};

export default AccountDashboard;
