import React, { useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const Deposit = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        accountNumber: "",
        amount: "",
        transactionType: "deposit", // Default transaction type
    });
    const [error, setError] = useState("");

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleDeposit = () => {
        // Send the form data with accountNumber, amount, and transactionType
        axios.post(`http://localhost:8000/deposit/`, {
            account_number: formData.accountNumber,
            amount: formData.amount,
            transaction_type: formData.transactionType,
        })
            .then(response => {
                alert(response.data.status);
                navigate("/"); // Redirect after successful deposit
            })
            .catch(error => {
                setError(error.response?.data?.error || "An error occurred");
            });
    };

    return (
        <div>
            <h2>Deposit Money</h2>
            <form>
                <input
                    type="text"
                    name="accountNumber"
                    value={formData.accountNumber}
                    onChange={handleInputChange}
                    placeholder="Account Number"
                    required
                />
                <input
                    type="number"
                    name="amount"
                    value={formData.amount}
                    onChange={handleInputChange}
                    placeholder="Amount"
                    required
                />
                <select
                    name="transactionType"
                    value={formData.transactionType}
                    onChange={handleInputChange}
                    required
                >
                    <option value="deposit">Deposit</option>
                    <option value="withdrawal">Withdrawal</option>
                    <option value="transfer">Transfer</option>
                </select>
                <button type="button" onClick={handleDeposit}>Submit</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Deposit;
