import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Transfer = () => {
    const navigate = useNavigate();
    const [amount, setAmount] = useState("");
    const [sourceAccountNumber, setSourceAccountNumber] = useState("");
    const [targetAccountNumber, setTargetAccountNumber] = useState("");
    const [error, setError] = useState("");
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8000/")
            .then(response => setAccounts(response.data))
            .catch(error => setError("Error fetching accounts"));
    }, []);

    const handleTransfer = () => {
        axios.post("http://localhost:8000/transfer/", {
            amount,
            source_account_number: sourceAccountNumber,
            target_account_number: targetAccountNumber,
        })
        .then(response => {
            alert(response.data.status);
            navigate("/"); // Redirect after successful transfer
        })
        .catch(error => {
            setError(error.response?.data?.error || "An error occurred");
        });
    };

    return (
        <div>
            <h2>Transfer Money</h2>
            <select
                value={sourceAccountNumber}
                onChange={(e) => setSourceAccountNumber(e.target.value)}
            >
                <option value="">Select Source Account</option>
                {accounts.map(account => (
                    <option key={account.id} value={account.account_number}>
                        {account.account_number} - {account.balance} USD
                    </option>
                ))}
            </select>
            <input
                type="number"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Amount"
            />
            <input
                type="text"
                value={targetAccountNumber}
                onChange={(e) => setTargetAccountNumber(e.target.value)}
                placeholder="Target Account Number"
            />
            <button onClick={handleTransfer}>Transfer</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Transfer;
