import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Deposit = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        accountNumber: "",
        amount: "",
        transactionType: "deposit", // Default transaction type
    });
    const [error, setError] = useState("");
    const [accounts, setAccounts] = useState([]); // State to store account numbers

    useEffect(() => {
        // Fetch the account numbers from the backend
        axios.get("http://127.0.0.1:8000/")
            .then(response => {
                console.log(response.data);
                setAccounts(response.data); // Assuming the response contains an array of account objects
            })
            .catch(error => {
                console.error("There was an error fetching the accounts!", error);
            });
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleDeposit = () => {
        // Send the form data with accountNumber, amount, and transactionType
        axios.post(`http://127.0.0.1:8000/deposit/`, {
            account_number: formData.accountNumber,
            amount: formData.amount,
            transaction_type: formData.transactionType,
        })
            .then(response => {
                alert(response.data.status);
                navigate("/dashboard"); // Redirect after successful deposit
            })
            .catch(error => {
                setError(error.response?.data?.error || "An error occurred");
            });
    };

    return (
        <div>
            <h2>Deposit Money</h2>
            <form>
                <select
                    name="accountNumber"
                    value={formData.accountNumber}
                    onChange={handleInputChange}
                    required
                >
                    <option value="" disabled>Select Account Number</option>
                    {accounts.map(account => (
                        <option key={account.id} value={account.account_number}>
                            {account.account_number}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    name="amount"
                    value={formData.amount}
                    onChange={handleInputChange}
                    placeholder="Amount"
                    required
                />
                <select
                    name="transactionType"
                    value={formData.transactionType}
                    onChange={handleInputChange}
                    required
                >
                    <option value="deposit">Deposit</option>
                    <option value="withdrawal">Withdrawal</option>
                    <option value="transfer">Transfer</option>
                </select>
                <button type="button" onClick={handleDeposit}>Submit</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Deposit;
