import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AccountDashboard.css"; // Import the CSS file

axios.defaults.baseURL = 'http://localhost:8000';

const AccountDashboard = () => {
    const [account, setAccount] = useState(null);
    const [amount, setAmount] = useState("");
    const [targetAccountNumber, setTargetAccountNumber] = useState("");

    useEffect(() => {
        // Fetch account details when the component mounts
        axios
            .get("/api/accounts/1/")
            .then((response) => setAccount(response.data))
            .catch((error) => console.error("Error fetching account:", error));
    }, []);

    const handleDeposit = () => {
        axios
            .post("/api/accounts/1/deposit/", { amount: parseFloat(amount) })
            .then((response) => {
                alert(response.data.status);
                setAmount(""); // Clear the input field
                refreshAccountDetails(); // Refresh account details after deposit
            })
            .catch((error) => console.error("Error depositing money:", error));
    };

    const handleWithdraw = () => {
        axios
            .post("/api/accounts/1/withdraw/", { amount: parseFloat(amount) })
            .then((response) => {
                alert(response.data.status);
                setAmount(""); // Clear the input field
                refreshAccountDetails(); // Refresh account details after withdrawal
            })
            .catch((error) => console.error("Error withdrawing money:", error));
    };

    const handleTransfer = () => {
        axios
            .post("/api/accounts/1/transfer/", {
                amount: parseFloat(amount),
                target_account_number: targetAccountNumber,
            })
            .then((response) => {
                alert(response.data.status);
                setAmount(""); // Clear the input field
                setTargetAccountNumber(""); // Clear the target account field
                refreshAccountDetails(); // Refresh account details after transfer
            })
            .catch((error) => console.error("Error transferring money:", error));
    };

    const refreshAccountDetails = () => {
        // Fetch account details again to update balance after any transaction
        axios
            .get("/api/accounts/1/")
            .then((response) => setAccount(response.data))
            .catch((error) => console.error("Error fetching account:", error));
    };

    return (
        <div className="dashboard-container">
            {account && (
                <div className="account-info">
                    <h2>Account: {account.account_number}</h2>
                    <h3>Balance: {account.balance}</h3>
                </div>
            )}
            <div className="actions">
                <h3>Actions</h3>
                <div className="form-group">
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        placeholder="Amount"
                        className="input-field"
                    />
                    <button onClick={handleDeposit} className="action-button">Deposit</button>
                    <button onClick={handleWithdraw} className="action-button">Withdraw</button>
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        value={targetAccountNumber}
                        onChange={(e) => setTargetAccountNumber(e.target.value)}
                        placeholder="Target Account Number"
                        className="input-field"
                    />
                    <button onClick={handleTransfer} className="action-button">Transfer</button>
                </div>
            </div>
        </div>
    );
};

export default AccountDashboard;
